import React, { useState } from "react";

import ExpenseForm from "./ExpenseForm";
import "./NewExpense.css";

const NewExpense = (props) => {
  const [style, setStyle] = useState({ display: "block" });
  const [expenseStyle, setExpenseStyle] = useState({ display: "none" });
  const clickHandler = () => {
    setExpenseStyle({ display: "block" });
    setStyle({ display: "none" });
  };
  const cancelDataHandler = (data) => {
    const cancel = data;
    {
      (() => {
        if (cancel.includes("Cancel")) return setStyle({ display: "block" });
        if (cancel.includes("Cancel"))
          return setExpenseStyle({ display: "none" });
      })();
    }
  };
  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    props.onAddExpense(expenseData);

    console.log(enteredExpenseData);
  };
  return (
    <div className="new-expense">
      <button onClick={clickHandler} style={style}>
        Add New Expense
      </button>
      <ExpenseForm
        onCancelData={cancelDataHandler}
        onSaveExpenseData={saveExpenseDataHandler}
        style={expenseStyle}
      />
    </div>
  );
};

export default NewExpense;
